/**
 * index.js - Application Entry Point
 * =========================================================================
 * Primary entry point for the Advanced Sorting Algorithm Visualization Platform.
 * This module initializes the React application, establishes the rendering context,
 * applies global styling, and implements the root component mounting logic.
 * 
 * The architectural design follows the Single Responsibility Principle,
 * focusing exclusively on application bootstrapping and delegating all
 * application-specific logic to dedicated components.
 * 
 * @module index
 * @author Advanced Algorithms Laboratory
 * @version 1.0.0
 * @license MIT
 */

// -----------------------------------------------------------------------------
// Standard Library and External Dependencies
// -----------------------------------------------------------------------------

import React from 'react';
import { createRoot } from 'react-dom/client';

// -----------------------------------------------------------------------------
// Global Style Application
// -----------------------------------------------------------------------------

/**
 * Global styles establish the foundational visual language and typographic
 * hierarchy of the application. The index.css file implements a comprehensive
 * CSS architecture following the Inverted Triangle CSS (ITCSS) methodology.
 */
import './styles/index.css';

// -----------------------------------------------------------------------------
// Application Root Component
// -----------------------------------------------------------------------------

import App from './App';

// -----------------------------------------------------------------------------
// Error Boundary Implementation
// -----------------------------------------------------------------------------

/**
 * GlobalErrorBoundary provides top-level exception handling for the React
 * component tree, ensuring graceful degradation in production environments
 * while exposing detailed diagnostic information during development.
 * 
 * @extends React.Component
 */
class GlobalErrorBoundary extends React.Component {
  constructor(props) {
    super(props);
    this.state = { hasError: false, error: null, errorInfo: null };
  }

  /**
   * Captures exceptions propagating through the component tree and
   * updates state to trigger fallback UI rendering.
   * 
   * @param {Error} error - The exception that was thrown
   * @returns {Object} Updated state reflecting error condition
   */
  static getDerivedStateFromError(error) {
    return { hasError: true, error };
  }

  /**
   * Performs side effects after an error is caught, specifically
   * logging detailed diagnostic information to the console.
   * 
   * @param {Error} error - The exception that was thrown
   * @param {React.ErrorInfo} errorInfo - Component stack trace information
   */
  componentDidCatch(error, errorInfo) {
    console.error('Unhandled exception in component tree:', error);
    console.error('Component stack trace:', errorInfo.componentStack);
    this.setState({ errorInfo });
  }

  /**
   * Renders either the application normally or a fallback UI when
   * an exception has occurred in the component tree.
   * 
   * @returns {React.ReactNode} The rendered component
   */
  render() {
    if (this.state.hasError) {
      return (
        <div className="error-boundary-container">
          <h1>Algorithm Visualization Platform</h1>
          <div className="error-panel">
            <h2>An error has occurred</h2>
            <p>
              The application encountered an unexpected error. 
              Please refresh the page to try again.
            </p>
            {process.env.NODE_ENV === 'development' && (
              <details>
                <summary>Technical Details</summary>
                <pre>{this.state.error && this.state.error.toString()}</pre>
                <pre>{this.state.errorInfo && this.state.errorInfo.componentStack}</pre>
              </details>
            )}
          </div>
        </div>
      );
    }

    return this.props.children;
  }
}

// -----------------------------------------------------------------------------
// Application Initialization and Mounting
// -----------------------------------------------------------------------------

/**
 * Initialize the application by mounting the root component tree to the DOM.
 * This implementation uses the modern createRoot API from React 18 for
 * concurrent rendering capabilities, with a fallback for React 17 compatibility.
 */
(function initializeApplication() {
  // Identify the DOM element that will host our React application
  const rootElement = document.getElementById('root');

  // Validate root element existence to prevent null reference exceptions
  if (!rootElement) {
    console.error(
      'Fatal initialization error: Unable to locate root DOM element. ' +
      'Ensure the HTML document includes an element with id="root".'
    );
    return;
  }

  try {
    // Create a root using the modern React 18 API
    const root = createRoot(rootElement);

    // Render the application within an error boundary for resilience
    root.render(
      <React.StrictMode>
        <GlobalErrorBoundary>
          <App />
        </GlobalErrorBoundary>
      </React.StrictMode>
    );

    console.info(
      'Algorithm Visualization Platform initialized successfully in ' +
      `${process.env.NODE_ENV} mode.`
    );
  } catch (error) {
    // Handle initialization errors gracefully
    console.error('Application initialization failed:', error);

    // Attempt to render a minimal error message directly
    rootElement.innerHTML = `
      <div style="font-family: system-ui, sans-serif; padding: 2rem; text-align: center;">
        <h1>Algorithm Visualization Platform</h1>
        <div style="background: #ffebee; border: 1px solid #ef9a9a; border-radius: 4px; padding: 1rem; margin: 2rem auto; max-width: 600px; text-align: left;">
          <h2 style="margin-top: 0;">Initialization Error</h2>
          <p>The application could not be initialized due to a technical issue.</p>
          <p>Please ensure you're using a modern browser and try refreshing the page.</p>
        </div>
      </div>
    `;
  }
})();

/**
 * Performance monitoring and analytics initialization
 * Only enabled in production environments to avoid development overhead
 */
if (process.env.NODE_ENV === 'production') {
  // Register performance observer to monitor critical rendering metrics
  const perfObserver = new PerformanceObserver((list) => {
    const metrics = {};

    list.getEntries().forEach(entry => {
      metrics[entry.name] = entry.startTime;
    });

    // Report metrics to monitoring system (implementation-specific)
    console.info('Performance metrics:', metrics);
  });

  // Observe core web vitals and other key metrics
  perfObserver.observe({ entryTypes: ['paint', 'largest-contentful-paint'] });
}
