// src/visualization/components/HeapVisualizer.js

import React, { useRef, useEffect, useMemo } from 'react';
import PropTypes from 'prop-types';

/**
 * HeapVisualizer Component
 * 
 * A specialized visualization component that renders the implicit binary tree
 * structure of a heap, enabling intuitive understanding of heap operations through
 * visual representation of parent-child relationships, node values, and heap property
 * enforcement.
 * 
 * This implementation applies computational geometry principles to accurately position
 * nodes according to complete binary tree topology, with O(n) time complexity for
 * rendering and O(1) space complexity beyond the input structure.
 * 
 * @author Advanced Sorting Visualization Platform
 * @version 1.0.0
 */
const HeapVisualizer = ({ 
  heapStructure, 
  width = 600, 
  height = 400, 
  nodeRadius = 25,
  highlightColor = '#FF5722',
  nodeColor = '#3F51B5',
  leafColor = '#4CAF50',
  textColor = '#FFFFFF'
}) => {
  // Reference to the canvas element for imperative rendering
  const canvasRef = useRef(null);

  /**
   * Pre-compute rendering constants for mathematical precision
   * This optimization prevents recalculation during render operations
   */
  const renderConstants = useMemo(() => {
    if (!heapStructure) return null;

    // Calculate max depth of the heap for level scaling
    const maxLevel = Math.max(...(heapStructure?.nodes || []).map(node => node.level), 0);

    return {
      // Vertical spacing between tree levels, accounting for node size and padding
      levelHeight: height / (maxLevel + 2),

      // Horizontal padding to ensure tree is centered
      xPadding: 20,

      // Available width for node positioning
      availableWidth: width - 2 * 20, // 20px padding on each side

      // Maximum tree depth for scaling calculations
      maxLevel
    };
  }, [heapStructure, width, height]);

  /**
   * Canvas rendering effect that executes whenever the heap structure
   * or rendering parameters change, implementing the visualization algorithm
   */
  useEffect(() => {
    // Guard clause to prevent rendering without data or canvas
    if (!heapStructure || !canvasRef.current || !renderConstants) return;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    // Clear canvas with optimal buffer clearing method
    ctx.clearRect(0, 0, width, height);

    // Destructure rendering constants for clarity
    const { levelHeight, xPadding, availableWidth, maxLevel } = renderConstants;

    // Scientific visualization principle: render background elements first
    renderEdges(ctx, heapStructure, levelHeight, xPadding, availableWidth);

    // Then render foreground elements (nodes)
    renderNodes(ctx, heapStructure, levelHeight, xPadding, availableWidth);

    // Finally, render informational overlay elements
    renderInformation(ctx);

  }, [heapStructure, width, height, nodeRadius, highlightColor, nodeColor, 
      leafColor, textColor, renderConstants]);

  /**
   * Renders the edges connecting nodes in the binary heap
   * Implements precise geometric positioning based on complete binary tree properties
   * 
   * @param {CanvasRenderingContext2D} ctx - Canvas rendering context
   * @param {Object} heapStructure - Heap structure data
   * @param {number} levelHeight - Vertical distance between levels
   * @param {number} xPadding - Horizontal padding
   * @param {number} availableWidth - Available width for rendering
   */
  const renderEdges = (ctx, heapStructure, levelHeight, xPadding, availableWidth) => {
    ctx.strokeStyle = '#888888';
    ctx.lineWidth = 2;

    // For each edge in the heap structure
    heapStructure.edges.forEach(edge => {
      const fromNode = heapStructure.nodes.find(n => n.id === edge.from);
      const toNode = heapStructure.nodes.find(n => n.id === edge.to);

      if (!fromNode || !toNode) return;

      // Calculate node positions using mathematical formula for complete binary tree
      // This formula guarantees proper node distribution at each level using powers of 2
      const fromX = xPadding + (fromNode.id + 1) * availableWidth / Math.pow(2, fromNode.level + 1);
      const fromY = (fromNode.level + 1) * levelHeight;

      const toX = xPadding + (toNode.id + 1) * availableWidth / Math.pow(2, toNode.level + 1);
      const toY = (toNode.level + 1) * levelHeight;

      // Draw edge with anti-aliasing for visual clarity
      ctx.beginPath();
      ctx.moveTo(fromX, fromY);
      ctx.lineTo(toX, toY);
      ctx.stroke();

      // Draw edge type label (left/right) at midpoint
      const midX = (fromX + toX) / 2;
      const midY = (fromY + toY) / 2;

      ctx.fillStyle = '#888888';
      ctx.font = '10px Arial';
      ctx.textAlign = 'center';
      ctx.textBaseline = 'middle';
      ctx.fillText(edge.type, midX, midY - 8);
    });
  };

  /**
   * Renders the nodes of the binary heap with appropriate styling and information
   * 
   * @param {CanvasRenderingContext2D} ctx - Canvas rendering context
   * @param {Object} heapStructure - Heap structure data
   * @param {number} levelHeight - Vertical distance between levels
   * @param {number} xPadding - Horizontal padding
   * @param {number} availableWidth - Available width for rendering
   */
  const renderNodes = (ctx, heapStructure, levelHeight, xPadding, availableWidth) => {
    heapStructure.nodes.forEach(node => {
      // Calculate node position with perfect binary tree spacing algorithm
      const x = xPadding + (node.id + 1) * availableWidth / Math.pow(2, node.level + 1);
      const y = (node.level + 1) * levelHeight;

      // Determine node fill color based on node state and type
      let fillColor = node.isLeaf ? leafColor : nodeColor;

      // Apply highlight if this node is the current focus of operation
      if (heapStructure.highlight !== undefined && node.id === heapStructure.highlight) {
        fillColor = highlightColor;
      }

      // Draw node circle with high-quality anti-aliasing
      ctx.beginPath();
      ctx.arc(x, y, nodeRadius, 0, 2 * Math.PI);
      ctx.fillStyle = fillColor;
      ctx.fill();

      // Draw node outline for visual definition
      ctx.strokeStyle = '#000000';
      ctx.lineWidth = 2;
      ctx.stroke();

      // Render node value with appropriate styling
      ctx.fillStyle = textColor;

      // Dynamically scale font size based on content length for readability
      const valueText = String(node.value);
      const fontSize = Math.max(10, Math.min(16, Math.floor(20 / Math.max(1, valueText.length))));

      ctx.font = `bold ${fontSize}px Arial`;
      ctx.textAlign = 'center';
      ctx.textBaseline = 'middle';
      ctx.fillText(valueText, x, y);

      // Render array index below node for educational context
      ctx.fillStyle = '#000000';
      ctx.font = '12px Arial';
      ctx.fillText(`idx: ${node.id}`, x, y + nodeRadius + 15);
    });
  };

  /**
   * Renders informational elements for educational context
   * 
   * @param {CanvasRenderingContext2D} ctx - Canvas rendering context
   */
  const renderInformation = (ctx) => {
    // Render heap property information
    ctx.fillStyle = '#333333';
    ctx.font = '14px Arial';
    ctx.textAlign = 'left';
    ctx.textBaseline = 'top';
    ctx.fillText('Binary Max Heap: parent ≥ children', 10, 10);

    // Draw legend for visual elements
    const legendY = height - 40;

    // Internal node representation
    ctx.beginPath();
    ctx.arc(30, legendY, 10, 0, 2 * Math.PI);
    ctx.fillStyle = nodeColor;
    ctx.fill();
    ctx.stroke();
    ctx.fillStyle = '#333333';
    ctx.textAlign = 'left';
    ctx.fillText('Internal Node', 50, legendY - 5);

    // Leaf node representation
    ctx.beginPath();
    ctx.arc(150, legendY, 10, 0, 2 * Math.PI);
    ctx.fillStyle = leafColor;
    ctx.fill();
    ctx.stroke();
    ctx.fillStyle = '#333333';
    ctx.fillText('Leaf Node', 170, legendY - 5);

    // Highlighted node representation
    ctx.beginPath();
    ctx.arc(270, legendY, 10, 0, 2 * Math.PI);
    ctx.fillStyle = highlightColor;
    ctx.fill();
    ctx.stroke();
    ctx.fillStyle = '#333333';
    ctx.fillText('Current Node', 290, legendY - 5);
  };

  // Render component with accessible structure and fallback content
  return (
    <div 
      className="heap-visualizer" 
      data-testid="heap-visualizer"
      aria-label="Binary heap visualization"
    >
      <canvas
        ref={canvasRef}
        width={width}
        height={height}
        className="heap-canvas"
        style={{ 
          border: '1px solid #ddd', 
          borderRadius: '4px',
          display: 'block'
        }}
        aria-hidden="true" // Canvas content described by surrounding elements
      />
      {!heapStructure && (
        <div 
          className="heap-placeholder" 
          style={{ 
            position: 'absolute', 
            top: '50%', 
            left: '50%', 
            transform: 'translate(-50%, -50%)',
            color: '#888',
            fontSize: '16px'
          }}
        >
          Awaiting heap structure data...
        </div>
      )}
    </div>
  );
};

// Rigorous PropTypes definition for component interface documentation
HeapVisualizer.propTypes = {
  /** The binary heap structure data object containing nodes and edges */
  heapStructure: PropTypes.shape({
    /** Array of node objects representing heap elements */
    nodes: PropTypes.arrayOf(PropTypes.shape({
      /** Unique identifier corresponding to array index */
      id: PropTypes.number.isRequired,
      /** Node value (heap key) */
      value: PropTypes.oneOfType([PropTypes.number, PropTypes.string]).isRequired,
      /** Tree level (depth) starting from 0 for root */
      level: PropTypes.number.isRequired,
      /** Whether this node is a leaf node (has no children) */
      isLeaf: PropTypes.bool.isRequired
    })).isRequired,
    /** Array of edge objects representing parent-child relationships */
    edges: PropTypes.arrayOf(PropTypes.shape({
      /** Source node id (parent) */
      from: PropTypes.number.isRequired,
      /** Target node id (child) */
      to: PropTypes.number.isRequired,
      /** Edge type ('left' or 'right') */
      type: PropTypes.oneOf(['left', 'right']).isRequired
    })).isRequired,
    /** Optional id of the currently highlighted node */
    highlight: PropTypes.number
  }),
  /** Canvas width in pixels */
  width: PropTypes.number,
  /** Canvas height in pixels */
  height: PropTypes.number,
  /** Radius of tree nodes in pixels */
  nodeRadius: PropTypes.number,
  /** Color for highlighted nodes (active operation) */
  highlightColor: PropTypes.string,
  /** Color for regular internal nodes */
  nodeColor: PropTypes.string,
  /** Color for leaf nodes */
  leafColor: PropTypes.string,
  /** Text color for node values */
  textColor: PropTypes.string
};

export default HeapVisualizer;
