/**
 * Algorithm Comparison Component
 * 
 * A sophisticated visualization component that facilitates side-by-side comparison
 * of algorithm implementations across JavaScript and Python environments, providing
 * deep insights into performance characteristics, operation counts, and execution patterns.
 * 
 * This component enables educational exploration of algorithmic behavior through:
 * - Synchronized visualization of execution steps
 * - Comparative performance metrics visualization
 * - Runtime characteristics analysis
 * - Implementation-specific optimization patterns
 */

import React, { useState, useEffect, useRef } from 'react';
import { WebGLRenderer } from '../visualization/renderers/webgl-renderer';
import { generateDataSet } from '../data/generators';

// Algorithm imports - reference through dedicated index files once complete
import BubbleSort from '../algorithms/comparison/bubble';
import MergeSort from '../algorithms/comparison/merge';
import QuickSort from '../algorithms/comparison/quick';
import HeapSort from '../algorithms/comparison/heap';

/**
 * Component for comparative analysis of sorting algorithm implementations
 * across JavaScript and Python environments.
 */
const AlgorithmComparison = ({ width = 1200, height = 600 }) => {
  // Algorithm selection state
  const [selectedAlgorithm, setSelectedAlgorithm] = useState('merge-sort');
  const [jsImplementation, setJsImplementation] = useState(null);

  // Visualization state
  const [jsCanvas, setJsCanvas] = useState(null);
  const [pyCanvas, setPyCanvas] = useState(null);
  const [jsRenderer, setJsRenderer] = useState(null);
  const [pyRenderer, setPyRenderer] = useState(null);

  // Data configuration
  const [selectedDataSet, setSelectedDataSet] = useState('random');
  const [dataSize, setDataSize] = useState(50);
  const [data, setData] = useState([]);

  // Comparison metrics
  const [comparisonResults, setComparisonResults] = useState(null);

  // Canvas references
  const jsCanvasRef = useRef(null);
  const pyCanvasRef = useRef(null);

  /**
   * Initialize component on mount
   */
  useEffect(() => {
    // Set canvas elements
    if (jsCanvasRef.current && pyCanvasRef.current) {
      setJsCanvas(jsCanvasRef.current);
      setPyCanvas(pyCanvasRef.current);
    }

    // Generate initial data
    generateInitialData();

    // Initialize algorithm implementation
    updateAlgorithmImplementation();

    return () => {
      // Clean up renderers on unmount
      if (jsRenderer) jsRenderer.dispose();
      if (pyRenderer) pyRenderer.dispose();
    };
  }, []);

  /**
   * Initialize WebGL renderers when canvases are available
   */
  useEffect(() => {
    if (jsCanvas && pyCanvas) {
      try {
        // Initialize JavaScript implementation renderer
        const jsRender = new WebGLRenderer(jsCanvas, {
          colorScheme: 'spectrum',
          barWidth: 8,
          spacing: 2,
          background: [0.1, 0.1, 0.2, 1.0]
        });
        setJsRenderer(jsRender);

        // Initialize Python implementation renderer
        const pyRender = new WebGLRenderer(pyCanvas, {
          colorScheme: 'heatmap',
          barWidth: 8,
          spacing: 2,
          background: [0.1, 0.2, 0.1, 1.0]
        });
        setPyRenderer(pyRender);

        // Set initial data if available
        if (data.length > 0) {
          jsRender.setData(data);
          pyRender.setData(data);
        }
      } catch (error) {
        console.error('Failed to initialize renderers:', error);
      }
    }
  }, [jsCanvas, pyCanvas, data]);

  /**
   * Update active algorithm implementation when selection changes
   */
  useEffect(() => {
    updateAlgorithmImplementation();
  }, [selectedAlgorithm]);

  /**
   * Generate initial dataset with selected parameters
   */
  const generateInitialData = () => {
    const newData = generateDataSet(selectedDataSet, dataSize, {
      min: 5,
      max: 100
    });
    setData(newData);
  };

  /**
   * Update the algorithm implementation based on current selection
   */
  const updateAlgorithmImplementation = () => {
    let implementation;

    switch (selectedAlgorithm) {
      case 'bubble-sort':
        implementation = new BubbleSort({ optimize: true, adaptive: true });
        break;
      case 'merge-sort':
        implementation = new MergeSort({
          adaptive: true,
          insertionThreshold: 10,
          optimizeMerge: true
        });
        break;
      case 'quick-sort':
        implementation = new QuickSort({
          pivotStrategy: 'median-of-three',
          insertionThreshold: 10,
          threeWayPartition: true
        });
        break;
      case 'heap-sort':
        implementation = new HeapSort({
          visualizeHeap: true,
          optimizeLeafChecks: true
        });
        break;
      default:
        implementation = new MergeSort();
    }

    setJsImplementation(implementation);
  };

  /**
   * Run comparison between JavaScript and Python implementations
   * Note: Simplified implementation until Python bridge is complete
   */
  const runComparison = async () => {
    if (!jsImplementation) return;

    // Execute JS implementation to gather metrics
    const jsStartTime = performance.now();
    const jsSortedData = jsImplementation.execute([...data]);
    const jsEndTime = performance.now();

    // Simulate Python results until bridge is complete
    // This will be replaced with actual Python execution through the bridge
    const pyExecutionTime = (jsImplementation.metrics.comparisons / 100) * 
                           (Math.random() * 0.5 + 0.8); // Simulated relative performance

    // Synthetic comparison results for demonstration
    setComparisonResults({
      algorithm: selectedAlgorithm,
      dataSize: data.length,
      dataType: selectedDataSet,
      javascript: {
        executionTime: jsEndTime - jsStartTime,
        metrics: jsImplementation.metrics
      },
      python: {
        executionTime: pyExecutionTime,
        metrics: {
          comparisons: Math.floor(jsImplementation.metrics.comparisons * (Math.random() * 0.2 + 0.9)),
          swaps: Math.floor(jsImplementation.metrics.swaps * (Math.random() * 0.2 + 0.9)),
          reads: Math.floor(jsImplementation.metrics.reads * (Math.random() * 0.2 + 0.9)),
          writes: Math.floor(jsImplementation.metrics.writes * (Math.random() * 0.2 + 0.9))
        }
      },
      comparison: {
        speedRatio: (jsEndTime - jsStartTime) / pyExecutionTime,
        accuracyMatch: true
      }
    });

    // Update renderers with sorted data
    if (jsRenderer) jsRenderer.setData(jsSortedData);
    if (pyRenderer) pyRenderer.setData(jsSortedData); // Using same data until Python bridge is complete
  };

  /**
   * Handle algorithm change from UI
   */
  const handleAlgorithmChange = (e) => {
    setSelectedAlgorithm(e.target.value);
  };

  /**
   * Handle data type change from UI
   */
  const handleDataTypeChange = (e) => {
    setSelectedDataSet(e.target.value);
  };

  /**
   * Handle data size change from UI
   */
  const handleDataSizeChange = (e) => {
    setDataSize(parseInt(e.target.value, 10));
  };

  /**
   * Regenerate dataset with current settings
   */
  const handleRegenerateData = () => {
    generateInitialData();
  };

  /**
   * Run comparison with current settings
   */
  const handleRunComparison = () => {
    runComparison();
  };

  /**
   * Format execution time for display
   */
  const formatTime = (ms) => {
    if (ms < 1) return `${(ms * 1000).toFixed(2)} μs`;
    if (ms < 1000) return `${ms.toFixed(2)} ms`;
    return `${(ms / 1000).toFixed(2)} s`;
  };

  return (
    <div className="algorithm-comparison">
      <div className="comparison-controls">
        <div className="control-section">
          <h3>Algorithm Selection</h3>
          <select value={selectedAlgorithm} onChange={handleAlgorithmChange}>
            <option value="bubble-sort">Bubble Sort</option>
            <option value="merge-sort">Merge Sort</option>
            <option value="quick-sort">Quick Sort</option>
            <option value="heap-sort">Heap Sort</option>
          </select>
        </div>

        <div className="control-section">
          <h3>Data Configuration</h3>
          <div className="control-group">
            <label>Data Type:</label>
            <select value={selectedDataSet} onChange={handleDataTypeChange}>
              <option value="random">Random</option>
              <option value="nearly-sorted">Nearly Sorted</option>
              <option value="reversed">Reversed</option>
              <option value="few-unique">Few Unique Values</option>
            </select>
          </div>

          <div className="control-group">
            <label>Data Size:</label>
            <select value={dataSize} onChange={handleDataSizeChange}>
              <option value="10">Small (10 elements)</option>
              <option value="50">Medium (50 elements)</option>
              <option value="100">Large (100 elements)</option>
              <option value="250">X-Large (250 elements)</option>
            </select>
          </div>

          <button onClick={handleRegenerateData}>Generate New Data</button>
        </div>

        <div className="control-section">
          <h3>Comparison</h3>
          <button onClick={handleRunComparison} className="primary-button">
            Run Comparison
          </button>
          <div className="note">
            <strong>Note:</strong> Python bridge integration is pending. 
            Comparison currently shows simulated Python metrics.
          </div>
        </div>
      </div>

      <div className="visualization-container">
        <div className="visualization-section">
          <h3>JavaScript Implementation</h3>
          <canvas
            ref={jsCanvasRef}
            width={width / 2 - 20}
            height={300}
            className="visualization-canvas"
          />
        </div>

        <div className="visualization-section">
          <h3>Python Implementation</h3>
          <canvas
            ref={pyCanvasRef}
            width={width / 2 - 20}
            height={300}
            className="visualization-canvas"
          />
        </div>
      </div>

      {comparisonResults && (
        <div className="comparison-results">
          <h3>Performance Comparison</h3>

          <div className="results-grid">
            <div className="metric-section">
              <h4>JavaScript Performance</h4>
              <div className="metric">
                <span className="metric-label">Execution Time:</span>
                <span className="metric-value">{formatTime(comparisonResults.javascript.executionTime)}</span>
              </div>
              <div className="metric">
                <span className="metric-label">Comparisons:</span>
                <span className="metric-value">{comparisonResults.javascript.metrics.comparisons?.toLocaleString()}</span>
              </div>
              <div className="metric">
                <span className="metric-label">Swaps:</span>
                <span className="metric-value">{comparisonResults.javascript.metrics.swaps?.toLocaleString()}</span>
              </div>
            </div>

            <div className="metric-section">
              <h4>Python Performance</h4>
              <div className="metric">
                <span className="metric-label">Execution Time:</span>
                <span className="metric-value">{formatTime(comparisonResults.python.executionTime)}</span>
              </div>
              <div className="metric">
                <span className="metric-label">Comparisons:</span>
                <span className="metric-value">{comparisonResults.python.metrics.comparisons?.toLocaleString()}</span>
              </div>
              <div className="metric">
                <span className="metric-label">Swaps:</span>
                <span className="metric-value">{comparisonResults.python.metrics.swaps?.toLocaleString()}</span>
              </div>
            </div>

            <div className="metric-section comparison-summary">
              <h4>Relative Performance</h4>
              <div className="metric highlight">
                <span className="metric-label">Speed Ratio:</span>
                <span className="metric-value">
                  {comparisonResults.comparison.speedRatio < 1 
                    ? `Python is ${(1/comparisonResults.comparison.speedRatio).toFixed(2)}× faster`
                    : `JavaScript is ${comparisonResults.comparison.speedRatio.toFixed(2)}× faster`}
                </span>
              </div>
              <div className="metric">
                <span className="metric-label">Data Size:</span>
                <span className="metric-value">{comparisonResults.dataSize} elements</span>
              </div>
              <div className="metric">
                <span className="metric-label">Data Type:</span>
                <span className="metric-value">{comparisonResults.dataType}</span>
              </div>
            </div>
          </div>

          <div className="implementation-notes">
            <h4>Implementation Insights</h4>
            <p>
              Different language implementations of the same algorithm demonstrate varied performance characteristics
              due to language-specific optimizations, memory management strategies, and execution environments.
              JavaScript typically benefits from JIT compilation in the browser, while Python excels with its
              memory efficiency and implementation simplicity.
            </p>
            <p>
              The relative performance differential varies based on input size, data distribution, and algorithm complexity.
              For {selectedAlgorithm}, the specific implementation details that affect performance include:
              {selectedAlgorithm === 'quick-sort' && " pivot selection strategy, partitioning approach, and recursion handling."}
              {selectedAlgorithm === 'merge-sort' && " memory allocation patterns, recursion depth, and merge strategy optimization."}
              {selectedAlgorithm === 'heap-sort' && " heap representation, sift-down implementation, and element comparison efficiency."}
              {selectedAlgorithm === 'bubble-sort' && " early termination detection, swap operation efficiency, and loop optimization."}
            </p>
          </div>
        </div>
      )}
    </div>
  );
};

export default AlgorithmComparison;
