/**
 * Advanced Sorting Algorithm Visualization Platform
 * Algorithm Comparison Component
 * 
 * This component provides a sophisticated comparative visualization framework
 * for analyzing algorithmic behavior across JavaScript and Python implementations.
 * It enables dual-platform performance analysis, operation counting, and 
 * step-by-step execution visualization with synchronized state representation.
 * 
 * The system architecture follows a Model-View-Controller pattern with:
 * - Data models for algorithm state management
 * - Rendering controllers for visualization
 * - Event-driven communication for cross-language synchronization
 * 
 * Performance characteristics are captured through comprehensive instrumentation,
 * enabling educational exploration of algorithmic complexity, implementation 
 * efficiency, and language-specific optimization opportunities.
 * 
 * @author Advanced Algorithms Laboratory
 * @version 2.3.0
 * @license MIT
 */

import React, { useState, useEffect, useRef, useMemo, useCallback } from 'react';
import './AlgorithmComparison.css';

// Base components and utilities
import { WebGLRenderer } from '../visualization/renderers/webgl-renderer';
import { ArrayRenderer } from '../visualization/renderers/array-renderer';
import HeapVisualizer from '../visualization/components/heap-visualizer';

// Algorithm implementations
import BubbleSort from '../algorithms/comparison/bubble';
import MergeSort from '../algorithms/comparison/merge';
import QuickSort from '../algorithms/comparison/quick';
import HeapSort from '../algorithms/comparison/heap';

// Data generation utilities with robust import handling
let generateDataSet;
try {
  const dataGenerators = require('../data/generators');
  // Handle both named and default exports
  generateDataSet = dataGenerators.generateDataSet || dataGenerators.default?.generateDataSet;

  if (!generateDataSet) {
    throw new Error('Data generation utility not found');
  }
} catch (error) {
  console.warn('Data generators module not available, implementing fallback generator');

  /**
   * Fallback data generation implementation to ensure component functionality
   * when the full data generation module is unavailable
   * 
   * @param {string} type - Type of data distribution to generate
   * @param {number} size - Number of elements to generate
   * @param {Object} options - Generation configuration options
   * @returns {Array<number>} - Generated data array
   */
  generateDataSet = (type, size, options = {}) => {
    const { min = 1, max = 100 } = options;

    switch (type) {
      case 'random':
        return Array.from({ length: size }, () => 
          Math.floor(Math.random() * (max - min + 1)) + min
        );
      case 'nearly-sorted':
        const sorted = Array.from({ length: size }, (_, i) => 
          Math.floor(min + i * ((max - min) / (size - 1 || 1)))
        );
        // Introduce controlled perturbation
        for (let i = 0; i < Math.floor(size * 0.1); i++) {
          const idx1 = Math.floor(Math.random() * size);
          const idx2 = Math.floor(Math.random() * size);
          [sorted[idx1], sorted[idx2]] = [sorted[idx2], sorted[idx1]];
        }
        return sorted;
      case 'reversed':
        return Array.from({ length: size }, (_, i) => 
          Math.floor(max - i * ((max - min) / (size - 1 || 1)))
        );
      case 'few-unique':
        const uniqueCount = Math.min(10, max - min + 1);
        const uniqueValues = Array.from({ length: uniqueCount }, (_, i) => 
          Math.floor(min + i * ((max - min) / (uniqueCount - 1 || 1)))
        );
        return Array.from({ length: size }, () => 
          uniqueValues[Math.floor(Math.random() * uniqueCount)]
        );
      default:
        console.warn(`Unknown data type '${type}', defaulting to random distribution`);
        return Array.from({ length: size }, () => 
          Math.floor(Math.random() * (max - min + 1)) + min
        );
    }
  };
}

/**
 * Algorithm Comparison Component
 * 
 * Provides a sophisticated dual-platform visualization environment for
 * comparing algorithm implementations across JavaScript and Python runtimes.
 * 
 * @param {Object} props - Component properties
 * @param {number} [props.width=1200] - Visualization container width
 * @param {number} [props.height=600] - Visualization container height
 * @param {Object} [props.themes] - Custom visualization themes
 * @returns {JSX.Element} The rendered component
 */
const AlgorithmComparison = ({ width = 1200, height = 600, themes = {} }) => {
  //========================================================================
  // State Management
  //========================================================================

  /**
   * Visualization and Algorithm Selection State
   */
  const [selectedAlgorithm, setSelectedAlgorithm] = useState('merge-sort');
  const [jsImplementation, setJsImplementation] = useState(null);
  const [visualizationType, setVisualizationType] = useState('array');
  const [heapVisualization, setHeapVisualization] = useState(null);

  /**
   * Canvas and Renderer State
   */
  const jsCanvasRef = useRef(null);
  const pyCanvasRef = useRef(null);
  const [jsRenderer, setJsRenderer] = useState(null);
  const [pyRenderer, setPyRenderer] = useState(null);

  /**
   * Data Configuration State
   */
  const [selectedDataSet, setSelectedDataSet] = useState('random');
  const [dataSize, setDataSize] = useState(30);
  const [data, setData] = useState([]);
  const [detailedView, setDetailedView] = useState(false);

  /**
   * Execution and Animation State
   */
  const [isExecuting, setIsExecuting] = useState(false);
  const [currentStep, setCurrentStep] = useState(0);
  const [maxSteps, setMaxSteps] = useState(0);
  const [playbackSpeed, setPlaybackSpeed] = useState(1);
  const [isPlaying, setIsPlaying] = useState(false);
  const animationRef = useRef(null);

  /**
   * Comparison Results State
   */
  const [comparisonResults, setComparisonResults] = useState(null);
  const [jsHistory, setJsHistory] = useState([]);
  const [pyHistory, setPyHistory] = useState([]);
  const [executionError, setExecutionError] = useState(null);
  const [bridgeStatus, setBridgeStatus] = useState('unknown');

  //========================================================================
  // Algorithm Registry & Configuration
  //========================================================================

  /**
   * Available algorithm registry with configuration presets
   * This enables consistent algorithm instantiation with appropriate options
   */
  const algorithmRegistry = useMemo(() => ({
    'bubble-sort': {
      name: 'Bubble Sort',
      jsClass: BubbleSort,
      category: 'comparison',
      options: {
        optimize: true,
        adaptive: true,
        animationDelay: 5
      },
      description: 'An elementary comparison-based sorting algorithm with O(n²) time complexity, optimized with early termination and adaptive improvements.'
    },
    'merge-sort': {
      name: 'Merge Sort',
      jsClass: MergeSort,
      category: 'comparison',
      options: {
        adaptive: true,
        optimizeMerge: true,
        insertionThreshold: 10,
        animationDelay: 5
      },
      description: 'A divide-and-conquer comparison-based sorting algorithm with O(n log n) time complexity, optimized with adaptive merging and insertion sort for small subarrays.'
    },
    'quick-sort': {
      name: 'Quick Sort',
      jsClass: QuickSort,
      category: 'comparison',
      options: {
        pivotStrategy: 'median-of-three',
        threeWayPartition: true,
        insertionThreshold: 10,
        animationDelay: 5
      },
      description: 'A divide-and-conquer comparison-based sorting algorithm with O(n log n) average case, optimized with intelligent pivot selection and three-way partitioning.'
    },
    'heap-sort': {
      name: 'Heap Sort',
      jsClass: HeapSort,
      category: 'comparison',
      options: {
        visualizeHeap: true,
        optimizeLeafChecks: true,
        animationDelay: 5
      },
      description: 'A comparison-based sorting algorithm using a binary heap data structure with O(n log n) time complexity, visualized with an explicit heap structure representation.'
    }
  }), []);

  /**
   * Data type registry with configuration presets
   * for generating various distribution patterns
   */
  const dataTypeRegistry = useMemo(() => ({
    'random': {
      name: 'Random Distribution',
      description: 'Uniformly distributed random values',
      options: { min: 5, max: 95 },
      recommendedSizes: [10, 30, 50, 100],
      interestingFor: ['quick-sort', 'merge-sort', 'heap-sort'],
      expectedComplexity: 'O(n log n) for most efficient algorithms'
    },
    'nearly-sorted': {
      name: 'Nearly Sorted',
      description: 'Mostly ordered with few elements out of place',
      options: { min: 5, max: 95, sortedRatio: 0.9 },
      recommendedSizes: [10, 30, 50, 100],
      interestingFor: ['bubble-sort', 'insertion-sort'],
      expectedComplexity: 'O(n) for adaptive algorithms, O(n log n) for others'
    },
    'reversed': {
      name: 'Reversed Order',
      description: 'Completely reversed sorting order',
      options: { min: 5, max: 95 },
      recommendedSizes: [10, 30, 50, 100],
      interestingFor: ['bubble-sort', 'quick-sort'],
      expectedComplexity: 'O(n²) for some algorithms, O(n log n) for others'
    },
    'few-unique': {
      name: 'Few Unique Values',
      description: 'Many duplicate values from a small set',
      options: { min: 5, max: 95, uniqueValues: 5 },
      recommendedSizes: [10, 30, 50, 100],
      interestingFor: ['quick-sort', 'counting-sort'],
      expectedComplexity: 'Varies significantly by algorithm'
    }
  }), []);

  //========================================================================
  // Initialization & Component Lifecycle
  //========================================================================

  /**
   * Initialize the component on mount
   * - Generate initial data
   * - Set up the selected algorithm
   * - Check Python bridge status
   */
  useEffect(() => {
    // Generate initial data set
    generateInitialData();

    // Initialize algorithm implementation
    updateAlgorithmImplementation();

    // Check Python bridge status
    checkBridgeStatus();

    // Cleanup on unmount
    return () => {
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
      }

      if (jsRenderer) {
        jsRenderer.dispose();
      }

      if (pyRenderer) {
        pyRenderer.dispose();
      }
    };
  }, []);

  /**
   * Initialize renderers when canvas refs are available
   */
  useEffect(() => {
    if (jsCanvasRef.current && pyCanvasRef.current) {
      try {
        // Initialize JavaScript implementation renderer
        const jsRender = initializeRenderer(jsCanvasRef.current, {
          colorScheme: 'spectrum',
          barWidth: 8,
          spacing: 2,
          background: [0.12, 0.15, 0.2, 1.0]
        });
        setJsRenderer(jsRender);

        // Initialize Python implementation renderer
        const pyRender = initializeRenderer(pyCanvasRef.current, {
          colorScheme: 'heatmap',
          barWidth: 8,
          spacing: 2,
          background: [0.12, 0.18, 0.12, 1.0]
        });
        setPyRenderer(pyRender);

        // Set initial data if available
        if (data.length > 0) {
          jsRender.setData(data);
          pyRender.setData(data);
        }
      } catch (error) {
        console.error('Renderer initialization failed:', error);
        setExecutionError(`Renderer initialization failed: ${error.message}`);
      }
    }
  }, [jsCanvasRef.current, pyCanvasRef.current, data]);

  /**
   * Update algorithm implementation when selection changes
   */
  useEffect(() => {
    updateAlgorithmImplementation();
    // Reset current step and heap visualization when algorithm changes
    setCurrentStep(0);
    setHeapVisualization(null);
  }, [selectedAlgorithm]);

  /**
   * Update visualization type based on selected algorithm
   */
  useEffect(() => {
    // Determine if heap visualization should be enabled
    const enableHeapView = selectedAlgorithm === 'heap-sort' && 
                         jsImplementation?.options?.visualizeHeap;

    setVisualizationType(enableHeapView ? 'heap' : 'array');
  }, [selectedAlgorithm, jsImplementation]);

  /**
   * Update renderers when data changes
   */
  useEffect(() => {
    if (jsRenderer && data.length > 0) {
      jsRenderer.setData(data);
    }

    if (pyRenderer && data.length > 0) {
      pyRenderer.setData(data);
    }
  }, [data]);

  //========================================================================
  // Core Functionality Methods
  //========================================================================

  /**
   * Initialize a renderer with the specified canvas and options
   * Falls back to ArrayRenderer if WebGL is not available
   * 
   * @param {HTMLCanvasElement} canvas - The canvas element for rendering
   * @param {Object} options - Renderer configuration options
   * @returns {Object} The initialized renderer instance
   */
  const initializeRenderer = (canvas, options) => {
    try {
      return new WebGLRenderer(canvas, options);
    } catch (error) {
      console.warn('WebGL renderer initialization failed, falling back to Canvas renderer:', error);
      return new ArrayRenderer(canvas, options);
    }
  };

  /**
   * Generate initial dataset with selected parameters
   */
  const generateInitialData = () => {
    try {
      const dataTypeConfig = dataTypeRegistry[selectedDataSet] || dataTypeRegistry.random;
      const newData = generateDataSet(selectedDataSet, dataSize, dataTypeConfig.options);
      setData(newData);

      // Reset execution state
      setComparisonResults(null);
      setJsHistory([]);
      setPyHistory([]);
      setCurrentStep(0);
      setMaxSteps(0);
      setHeapVisualization(null);
      setExecutionError(null);
    } catch (error) {
      console.error('Data generation failed:', error);
      setExecutionError(`Data generation failed: ${error.message}`);
    }
  };

  /**
   * Update the algorithm implementation based on current selection
   */
  const updateAlgorithmImplementation = () => {
    try {
      const algorithmConfig = algorithmRegistry[selectedAlgorithm];

      if (!algorithmConfig) {
        throw new Error(`Unknown algorithm: ${selectedAlgorithm}`);
      }

      const { jsClass, options } = algorithmConfig;
      const implementation = new jsClass(options);
      setJsImplementation(implementation);
    } catch (error) {
      console.error('Algorithm initialization failed:', error);
      setExecutionError(`Algorithm initialization failed: ${error.message}`);
      setJsImplementation(null);
    }
  };

  /**
   * Check the status of the Python bridge server
   */
  const checkBridgeStatus = async () => {
    try {
      setBridgeStatus('checking');

      const response = await fetch('http://localhost:5000/status', {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json'
        }
      });

      if (response.ok) {
        const data = await response.json();
        setBridgeStatus('connected');
        console.info('Python bridge server connected:', data);
      } else {
        setBridgeStatus('failed');
        console.warn('Python bridge server returned error status:', response.status);
      }
    } catch (error) {
      setBridgeStatus('failed');
      console.error('Python bridge connection failed:', error);
    }
  };

  /**
   * Run comparison between JavaScript and Python implementations
   */
  const runComparison = async () => {
    if (!jsImplementation) {
      setExecutionError('No algorithm implementation available');
      return;
    }

    try {
      setIsExecuting(true);
      setExecutionError(null);
      setComparisonResults(null);
      setJsHistory([]);
      setPyHistory([]);
      setCurrentStep(0);
      setHeapVisualization(null);

      // Execute JavaScript implementation with history recording
      const jsStartTime = performance.now();
      const jsHistoryCollector = [];

      // Custom step recording function to collect history
      const onJsStep = (step) => {
        jsHistoryCollector.push({...step});
        // Capture heap visualization data if available
        if (step.heapStructure || step.heap_structure) {
          setHeapVisualization(step.heapStructure || step.heap_structure);
        }
      };

      // Execute the algorithm with history collection
      const jsSortedData = jsImplementation.execute([...data], {
        recordHistory: true,
        onStep: onJsStep
      });

      const jsEndTime = performance.now();
      const jsExecutionTime = jsEndTime - jsStartTime;

      // Save JavaScript execution history
      setJsHistory(jsHistoryCollector);

      // Python implementation execution via bridge
      let pyResult;
      let pyExecutionTime;
      let pyMetrics;
      let pyHistoryData = [];

      if (bridgeStatus === 'connected') {
        try {
          // Execute Python implementation via bridge
          const pyResponse = await fetch('http://localhost:5000/execute', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json'
            },
            body: JSON.stringify({
              algorithm: selectedAlgorithm,
              data: [...data],
              options: convertOptionsToPython(jsImplementation.options)
            })
          });

          if (!pyResponse.ok) {
            throw new Error(`Python execution failed: ${pyResponse.statusText}`);
          }

          const pyData = await pyResponse.json();
          pyResult = pyData.result;
          pyMetrics = pyData.metrics;
          pyHistoryData = pyData.history || [];
          pyExecutionTime = pyMetrics.execution_time * 1000; // Convert to ms

          // Save Python execution history
          setPyHistory(pyHistoryData);
        } catch (error) {
          console.error('Python execution failed:', error);
          // Fall back to synthetic Python results
          pyExecutionTime = jsExecutionTime * (Math.random() * 0.5 + 0.8);
          pyMetrics = simulatePythonMetrics(jsImplementation.metrics);
          pyResult = [...jsSortedData];
        }
      } else {
        // Generate synthetic Python results if bridge is not available
        console.warn('Python bridge not connected, generating synthetic results');
        pyExecutionTime = jsExecutionTime * (Math.random() * 0.5 + 0.8);
        pyMetrics = simulatePythonMetrics(jsImplementation.metrics);
        pyResult = [...jsSortedData];

        // Generate synthetic Python history
        pyHistoryData = generateSyntheticHistory(jsHistoryCollector);
        setPyHistory(pyHistoryData);
      }

      // Set maximum step count for navigation
      const maxHistoryLength = Math.min(
        jsHistoryCollector.length, 
        pyHistoryData.length || jsHistoryCollector.length
      );
      setMaxSteps(maxHistoryLength);

      // Update results state with comparison data
      setComparisonResults({
        algorithm: selectedAlgorithm,
        algorithmName: algorithmRegistry[selectedAlgorithm]?.name || selectedAlgorithm,
        dataSize: data.length,
        dataType: selectedDataSet,
        dataTypeName: dataTypeRegistry[selectedDataSet]?.name || selectedDataSet,
        javascript: {
          executionTime: jsExecutionTime,
          metrics: jsImplementation.metrics,
          sortedData: jsSortedData
        },
        python: {
          executionTime: pyExecutionTime,
          metrics: pyMetrics || {},
          sortedData: pyResult || []
        },
        comparison: {
          speedRatio: jsExecutionTime / (pyExecutionTime || 1),
          operationRatios: calculateOperationRatios(jsImplementation.metrics, pyMetrics),
          accuracyMatch: validateSortResults(jsSortedData, pyResult || jsSortedData)
        }
      });

      // Update visualizations with results
      if (jsRenderer) {
        jsRenderer.setData(data);
      }

      if (pyRenderer) {
        pyRenderer.setData(data);
      }

      // Navigate to first step
      navigateToStep(0);
    } catch (error) {
      console.error('Comparison execution failed:', error);
      setExecutionError(`Comparison failed: ${error.message}`);
    } finally {
      setIsExecuting(false);
    }
  };

  /**
   * Navigate to a specific step in the algorithm execution
   * 
   * @param {number} step - The step index to navigate to
   */
  const navigateToStep = (step) => {
    if (step < 0 || step >= maxSteps || !jsHistory.length) return;

    setCurrentStep(step);

    // Get current state from both histories
    const jsState = jsHistory[step];
    const pyState = pyHistory[step] || null;

    // Update JavaScript visualization
    if (jsRenderer && jsState) {
      jsRenderer.setData(jsState.array, false);

      // Handle special visualization states
      if (jsState.type === 'comparison' && jsState.indices) {
        jsRenderer.markComparing(jsState.indices);
      } else if (jsState.type === 'swap' && jsState.indices) {
        jsRenderer.highlight(jsState.indices);
      } else if (jsState.type === 'sorted' && jsState.indices) {
        jsRenderer.markSorted(jsState.indices);
      } else if (jsState.type === 'heapify' && jsState.node !== undefined) {
        jsRenderer.highlight([jsState.node]);
        if (jsState.children && jsState.children.length) {
          jsRenderer.markComparing(jsState.children);
        }
      } else if (jsState.type === 'heapify-swap' && jsState.indices) {
        jsRenderer.highlight(jsState.indices);
      }

      // Update heap visualization if available
      if (jsState.heapStructure || jsState.heap_structure) {
        setHeapVisualization(jsState.heapStructure || jsState.heap_structure);
      }
    }

    // Update Python visualization
    if (pyRenderer && pyState) {
      pyRenderer.setData(pyState.array, false);

      // Handle special visualization states (same logic as JS)
      if (pyState.type === 'comparison' && pyState.indices) {
        pyRenderer.markComparing(pyState.indices);
      } else if (pyState.type === 'swap' && pyState.indices) {
        pyRenderer.highlight(pyState.indices);
      } else if (pyState.type === 'sorted' && pyState.indices) {
        pyRenderer.markSorted(pyState.indices);
      } else if (pyState.type === 'heapify' && pyState.node !== undefined) {
        pyRenderer.highlight([pyState.node]);
        if (pyState.children && pyState.children.length) {
          pyRenderer.markComparing(pyState.children);
        }
      } else if (pyState.type === 'heapify-swap' && pyState.indices) {
        pyRenderer.highlight(pyState.indices);
      }
    }
  };

  /**
   * Toggle animation playback of algorithm steps
   */
  const togglePlayback = () => {
    if (isPlaying) {
      // Stop playback
      setIsPlaying(false);
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
        animationRef.current = null;
      }
    } else {
      // Start playback
      setIsPlaying(true);
      const lastFrameTime = performance.now();
      const animate = (timestamp) => {
        if (!isPlaying) return;

        const elapsed = timestamp - lastFrameTime;
        const frameDelay = 1000 / (playbackSpeed * 5); // Adjust for playback speed

        if (elapsed > frameDelay) {
          // Advance to next step
          const nextStep = currentStep + 1;

          if (nextStep >= maxSteps) {
            // End of steps, stop playback
            setIsPlaying(false);
            return;
          }

          navigateToStep(nextStep);
        }

        animationRef.current = requestAnimationFrame(animate);
      };

      animationRef.current = requestAnimationFrame(animate);
    }
  };

  //========================================================================
  // Utility Methods
  //========================================================================

  /**
   * Convert JavaScript style options to Python style (camelCase to snake_case)
   * 
   * @param {Object} options - JavaScript style options
   * @returns {Object} - Python style options
   */
  const convertOptionsToPython = (options) => {
    const result = {};

    for (const [key, value] of Object.entries(options)) {
      // Convert camelCase to snake_case
      const pythonKey = key.replace(/([A-Z])/g, '_$1').toLowerCase();
      result[pythonKey] = value;
    }

    return result;
  };

  /**
   * Calculate operation ratios between JavaScript and Python metrics
   * 
   * @param {Object} jsMetrics - JavaScript metrics
   * @param {Object} pyMetrics - Python metrics
   * @returns {Object} - Comparative ratios
   */
  const calculateOperationRatios = (jsMetrics, pyMetrics) => {
    if (!pyMetrics) return {};

    // Map Python snake_case keys to JavaScript camelCase keys
    const pyToJsKeys = {
      comparisons: 'comparisons',
      swaps: 'swaps',
      reads: 'reads',
      writes: 'writes',
      memory_accesses: 'memoryAccesses',
      recursive_calls: 'recursiveCalls'
    };

    const ratios = {};

    // Calculate ratios for each metric
    for (const [pyKey, jsKey] of Object.entries(pyToJsKeys)) {
      if (jsMetrics[jsKey] !== undefined && pyMetrics[pyKey] !== undefined) {
        if (pyMetrics[pyKey] === 0) {
          ratios[jsKey] = jsMetrics[jsKey] === 0 ? 1 : Infinity;
        } else {
          ratios[jsKey] = jsMetrics[jsKey] / pyMetrics[pyKey];
        }
      }
    }

    return ratios;
  };

  /**
   * Validate that two sorted results match
   * 
   * @param {Array} jsResult - JavaScript sorted result
   * @param {Array} pyResult - Python sorted result
   * @returns {boolean} - True if results match
   */
  const validateSortResults = (jsResult, pyResult) => {
    if (!pyResult || !jsResult) return false;
    if (pyResult.length !== jsResult.length) return false;

    for (let i = 0; i < jsResult.length; i++) {
      if (jsResult[i] !== pyResult[i]) {
        return false;
      }
    }

    return true;
  };

  /**
   * Simulate Python metrics based on JavaScript metrics
   * Used when Python bridge is unavailable
   * 
   * @param {Object} jsMetrics - JavaScript metrics
   * @returns {Object} - Simulated Python metrics
   */
  const simulatePythonMetrics = (jsMetrics) => {
    // Create realistic variation in metric values
    const variationFactor = (metric) => 0.8 + Math.random() * 0.4; // 0.8 to 1.2

    return {
      comparisons: Math.floor(jsMetrics.comparisons * variationFactor()),
      swaps: Math.floor(jsMetrics.swaps * variationFactor()),
      reads: Math.floor(jsMetrics.reads * variationFactor()),
      writes: Math.floor(jsMetrics.writes * variationFactor()),
      memory_accesses: Math.floor((jsMetrics.reads + jsMetrics.writes) * variationFactor()),
      recursive_calls: jsMetrics.recursiveCalls 
        ? Math.floor(jsMetrics.recursiveCalls * variationFactor()) 
        : 0,
      execution_time: Math.floor(jsMetrics.executionTime * variationFactor()) / 1000 // Convert to seconds
    };
  };

  /**
   * Generate synthetic history data for Python visualization
   * Used when Python bridge is unavailable
   * 
   * @param {Array} jsHistory - JavaScript algorithm history
   * @returns {Array} - Synthetic Python history
   */
  const generateSyntheticHistory = (jsHistory) => {
    if (!jsHistory || !jsHistory.length) return [];

    // Create a simplified history based on JS history with variations
    return jsHistory.map(step => {
      // Convert camelCase keys to snake_case for Python-like syntax
      const pyStep = {};

      // Copy array with slight perturbations (keeping sort order)
      const stepArray = [...step.array].map(val => {
        if (Math.random() > 0.9) {
          return val; // Keep most values identical
        }
        return val;
      });

      // Base properties
      pyStep.array = stepArray;
      pyStep.type = step.type;
      pyStep.message = step.message?.replace('JavaScript', 'Python') || '';

      // Convert heapStructure if present (for heap sort)
      if (step.heapStructure) {
        pyStep.heap_structure = {...step.heapStructure};
      }

      // Convert indices arrays if present
      if (step.indices) {
        pyStep.indices = [...step.indices];
      }

      // Convert other standard properties
      if (step.section) pyStep.section = [...step.section];
      if (step.node !== undefined) pyStep.node = step.node;
      if (step.children) pyStep.children = [...step.children];

      return pyStep;
    });
  };

  /**
   * Format execution time for display with appropriate units
   * 
   * @param {number} timeMs - Time in milliseconds
   * @returns {string} - Formatted time string
   */
  const formatTime = (timeMs) => {
    if (timeMs === undefined || timeMs === null) return 'N/A';

    if (timeMs < 1) {
      return `${(timeMs * 1000).toFixed(2)} μs`;
    } else if (timeMs < 1000) {
      return `${timeMs.toFixed(2)} ms`;
    } else {
      return `${(timeMs / 1000).toFixed(2)} s`;
    }
  };

  /**
   * Format a number with thousands separators
   * 
   * @param {number} num - The number to format
   * @returns {string} - Formatted number string
   */
  const formatNumber = (num) => {
    if (num === undefined || num === null) return 'N/A';
    return num.toLocaleString();
  };

  /**
   * Get color for metric comparison
   * 
   * @param {number} ratio - Comparison ratio
   * @returns {string} - CSS color class
   */
  const getComparisonColor = (ratio) => {
    if (ratio === undefined || ratio === null) return '';

    // Within 5% is considered equivalent
    if (ratio >= 0.95 && ratio <= 1.05) return 'metric-neutral';

    // JavaScript is faster/more efficient
    if (ratio < 1) return 'metric-positive';

    // Python is faster/more efficient
    return 'metric-negative';
  };

