// src/visualization/components/heap-visualizer.js

import React, { useRef, useEffect } from 'react';

/**
 * HeapVisualizer Component
 * 
 * A specialized visualization component that renders a binary heap structure as a tree,
 * allowing for intuitive understanding of the implicit tree structure used in heap sort.
 * 
 * The component uses canvas-based rendering for maximum performance and supports:
 * - Visual representation of the binary tree structure
 * - Node highlighting for active operations
 * - Distinction between internal nodes and leaf nodes
 * - Proper mathematical positioning of nodes based on level
 * 
 * @param {Object} heapStructure - Binary heap representation data
 * @param {number} width - Canvas width
 * @param {number} height - Canvas height
 * @param {number} nodeRadius - Radius of tree nodes in pixels
 * @param {string} highlightColor - Color for highlighted nodes
 * @param {string} nodeColor - Color for regular internal nodes
 * @param {string} leafColor - Color for leaf nodes
 * @param {string} textColor - Color for node text
 */
const HeapVisualizer = ({ 
  heapStructure, 
  width = 600, 
  height = 400, 
  nodeRadius = 25,
  highlightColor = '#FF5722',
  nodeColor = '#3F51B5',
  leafColor = '#4CAF50',
  textColor = '#FFFFFF'
}) => {
  const canvasRef = useRef(null);

  /**
   * Canvas rendering effect that executes whenever the heap structure
   * or rendering parameters change
   */
  useEffect(() => {
    if (!heapStructure || !canvasRef.current) return;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    // Clear canvas with high-performance method
    ctx.clearRect(0, 0, width, height);

    // Calculate max depth of the heap for level positioning
    const maxLevel = Math.max(...heapStructure.nodes.map(node => node.level), 0);

    // Calculate layout parameters with mathematical precision
    const levelHeight = height / (maxLevel + 2);  // Vertical distance between levels
    const xPadding = 20;                         // Horizontal padding
    const availableWidth = width - 2 * xPadding; // Available width for nodes

    // Draw edges first (so they're behind nodes)
    renderEdges(ctx, heapStructure, maxLevel, levelHeight, xPadding, availableWidth);

    // Draw nodes with proper level-based positioning
    renderNodes(ctx, heapStructure, maxLevel, levelHeight, xPadding, availableWidth);

    // Draw informational elements
    renderInformation(ctx, heapStructure);

  }, [heapStructure, width, height, nodeRadius, highlightColor, nodeColor, leafColor, textColor]);

  /**
   * Renders the edges connecting nodes in the binary heap
   * 
   * @param {CanvasRenderingContext2D} ctx - Canvas rendering context
   * @param {Object} heapStructure - Heap structure data
   * @param {number} maxLevel - Maximum tree depth
   * @param {number} levelHeight - Vertical distance between levels
   * @param {number} xPadding - Horizontal padding
   * @param {number} availableWidth - Available width for rendering
   */
  const renderEdges = (ctx, heapStructure, maxLevel, levelHeight, xPadding, availableWidth) => {
    ctx.strokeStyle = '#888888';
    ctx.lineWidth = 2;

    heapStructure.edges.forEach(edge => {
      const fromNode = heapStructure.nodes.find(n => n.id === edge.from);
      const toNode = heapStructure.nodes.find(n => n.id === edge.to);

      if (!fromNode || !toNode) return;

      // Calculate node positions using mathematical formula for complete binary tree
      // This ensures proper node distribution at each level
      const fromX = xPadding + (fromNode.id + 1) * availableWidth / Math.pow(2, fromNode.level + 1);
      const fromY = (fromNode.level + 1) * levelHeight;

      const toX = xPadding + (toNode.id + 1) * availableWidth / Math.pow(2, toNode.level + 1);
      const toY = (toNode.level + 1) * levelHeight;

      // Draw edge with anti-aliasing
      ctx.beginPath();
      ctx.moveTo(fromX, fromY);
      ctx.lineTo(toX, toY);
      ctx.stroke();

      // Draw small label for edge type (left/right)
      const midX = (fromX + toX) / 2;
      const midY = (fromY + toY) / 2;

      ctx.fillStyle = '#888888';
      ctx.font = '10px Arial';
      ctx.textAlign = 'center';
      ctx.textBaseline = 'middle';
      ctx.fillText(edge.type, midX, midY - 8);
    });
  };

  /**
   * Renders the nodes of the binary heap
   * 
   * @param {CanvasRenderingContext2D} ctx - Canvas rendering context
   * @param {Object} heapStructure - Heap structure data
   * @param {number} maxLevel - Maximum tree depth
   * @param {number} levelHeight - Vertical distance between levels
   * @param {number} xPadding - Horizontal padding
   * @param {number} availableWidth - Available width for rendering
   */
  const renderNodes = (ctx, heapStructure, maxLevel, levelHeight, xPadding, availableWidth) => {
    heapStructure.nodes.forEach(node => {
      // Calculate node position based on complete binary tree structure
      const x = xPadding + (node.id + 1) * availableWidth / Math.pow(2, node.level + 1);
      const y = (node.level + 1) * levelHeight;

      // Determine node color based on node state and type
      let fillColor = node.isLeaf ? leafColor : nodeColor;

      // Highlight the focused node if specified
      if (heapStructure.highlight !== undefined && node.id === heapStructure.highlight) {
        fillColor = highlightColor;
      }

      // Draw node circle with anti-aliasing
      ctx.beginPath();
      ctx.arc(x, y, nodeRadius, 0, 2 * Math.PI);
      ctx.fillStyle = fillColor;
      ctx.fill();

      // Draw node outline for definition
      ctx.strokeStyle = '#000000';
      ctx.lineWidth = 2;
      ctx.stroke();

      // Draw node value with appropriate font scaling
      ctx.fillStyle = textColor;

      // Scale font size based on content length
      const valueText = node.value.toString();
      const fontSize = valueText.length > 2 ? 14 : 16;

      ctx.font = `bold ${fontSize}px Arial`;
      ctx.textAlign = 'center';
      ctx.textBaseline = 'middle';
      ctx.fillText(valueText, x, y);

      // Draw node index below (important for educational context)
      ctx.fillStyle = '#000000';
      ctx.font = '12px Arial';
      ctx.fillText(`idx: ${node.id}`, x, y + nodeRadius + 15);
    });
  };

  /**
   * Renders informational elements like heap property explanation and legend
   * 
   * @param {CanvasRenderingContext2D} ctx - Canvas rendering context
   */
  const renderInformation = (ctx) => {
    ctx.fillStyle = '#333333';
    ctx.font = '14px Arial';
    ctx.textAlign = 'left';
    ctx.textBaseline = 'top';
    ctx.fillText('Binary Max Heap: parent ≥ children', 10, 10);

    // Draw legend at bottom
    const legendY = height - 40;

    // Regular node
    ctx.beginPath();
    ctx.arc(30, legendY, 10, 0, 2 * Math.PI);
    ctx.fillStyle = nodeColor;
    ctx.fill();
    ctx.stroke();
    ctx.fillStyle = '#333333';
    ctx.textAlign = 'left';
    ctx.fillText('Internal Node', 50, legendY - 5);

    // Leaf node
    ctx.beginPath();
    ctx.arc(150, legendY, 10, 0, 2 * Math.PI);
    ctx.fillStyle = leafColor;
    ctx.fill();
    ctx.stroke();
    ctx.fillStyle = '#333333';
    ctx.fillText('Leaf Node', 170, legendY - 5);

    // Highlighted node
    ctx.beginPath();
    ctx.arc(270, legendY, 10, 0, 2 * Math.PI);
    ctx.fillStyle = highlightColor;
    ctx.fill();
    ctx.stroke();
    ctx.fillStyle = '#333333';
    ctx.fillText('Current Node', 290, legendY - 5);
  };

  return (
    <div className="heap-visualizer">
      <canvas
        ref={canvasRef}
        width={width}
        height={height}
        className="heap-canvas"
        style={{ border: '1px solid #ddd', borderRadius: '4px' }}
      />
      {!heapStructure && (
        <div className="heap-placeholder" style={{ 
          position: 'absolute', 
          top: '50%', 
          left: '50%', 
          transform: 'translate(-50%, -50%)',
          color: '#888',
          fontSize: '16px'
        }}>
          Waiting for heap structure data...
        </div>
      )}
    </div>
  );
};

export default HeapVisualizer;
